import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Inventory = () => {
  const [inventory, setInventory] = useState([]);
  const [client, setClient] = useState('');
  const [cate, setCate] = useState('');
  const [subcate, setSubcate] = useState('');
  const [responseMessage, setResponseMessage] = useState('');

  const fetchInventory = async () => {
    try {
        console.log(client);
        console.log(cate);
        console.log(subcate);
      const response = await axios.get("http://127.0.0.1:5000//api/inventory", {
        params: { client: client, mainCategory: cate , sub_category: subcate},
      });
      setInventory(response.data.inventory);
    } catch (error) {
      setResponseMessage(error.response?.data?.message || 'Error fetching inventory');
    }
  };

  useEffect(() => {
    if (client) fetchInventory();
  }, [client]);

  return (
    <div>
      <h2>Inventory</h2>
      <label>Client ID:</label>
      <input type="text" value={client} onChange={(e) => setClient(e.target.value)} />
      <label>Category:</label>
      <input type="text" value={cate} onChange={(e) => setCate(e.target.value)} />
      <label>Sub-Category:</label>
      <input type="text" value={subcate} onChange={(e) => setSubcate(e.target.value)} />
      <button onClick={fetchInventory}>Fetch Inventory</button>
      <p>{responseMessage}</p>
      <ul>
                    <table border="1" cellPadding="10">
                        <thead>
                            <tr>
                                <th>itemid</th>
                                <th>idescription</th>
                                <th>photo</th>
                                <th>isnew</th>
                                <th>haspieces</th>
                                <th>material</th>
                                <th>maincategory</th>
                                <th>subcategory</th>
                            </tr>
                        </thead>
                        <tbody>
                            {inventory.map((item) => (
                                <tr>
                                    <td>{item.itemid}</td>
                                    <td>{item.idescription}</td>
                                    <td>{item.photo}</td>
                                    <td>{item.isnew}</td>
                                    <td>{item.haspieces}</td>
                                    <td>{item.material}</td>
                                    <td>{item.maincategory}</td>
                                    <td>{item.subcategory}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
      </ul>
    </div>
  );
};

export default Inventory;
