import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Inventory = () => {
  const [inventory, setInventory] = useState([]);
  const [orderID, setOrderID] = useState('');
  const [client, setClient] = useState('');
  const [responseMessage, setResponseMessage] = useState('');

  const fetchInventory = async () => {
    try {
      const response = await axios.get('/api/inventory', {
        params: { OrderID: orderID, client: client },
      });
      setInventory(response.data.inventory);
    } catch (error) {
      setResponseMessage(error.response?.data?.message || 'Error fetching inventory');
    }
  };

  useEffect(() => {
    if (orderID || client) fetchInventory();
  }, [orderID, client]);

  return (
    <div>
      <h2>Inventory</h2>
      <label>Order ID:</label>
      <input type="text" value={orderID} onChange={(e) => setOrderID(e.target.value)} />
      <label>Client ID:</label>
      <input type="text" value={client} onChange={(e) => setClient(e.target.value)} />
      <button onClick={fetchInventory}>Fetch Inventory</button>
      <p>{responseMessage}</p>
      <ul>
        {inventory.map((item) => (
          <li key={item.itemid}>
            <strong>{item.idescription}</strong> (Category: {item.maincategory})
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Inventory;
